import React from 'react';
import PopupWithForm from './PopupWithForm';
import {popupInputClass} from '../utils/constants';

//---------------------------------------------------------------------------------------------------------------------

// Добавление карточки

function AddPlacePopup(props) {
    const [inputs, setInputs] = React.useState({ 
      title: { value: '', isValid: true, },
      link: { value: '', isValid: true, },
    });

    const [errorClasses, setErrorClasses] = React.useState({
      title: { input: '', error: '', }, 
      link: {  input: '', error: '', },
    });

    const [errorMessages, setErrorMessages] = React.useState({title: '', link: ''});

    React.useEffect(() => {
      setInputs({ 
        title: { value: '', isValid: false, },
        link: { value: '', isValid: false, },
      });
      setErrorClasses({
        title: { input: '', error: '', }, 
        link: { input: '', error: '', },
      });
    }, [props.isOpen]);

    const handleSubmit = (e) => {
      e.preventDefault();
      props.onAddPlace({title: inputs.name.value, link: inputs.link.value});
    };

    const handleChange = (e) => {
      const {name, value, validity} = e.target;
      setInputs({...inputs, [name]: { value: value, isValid: validity.valid, }});
    };

    const handleInput = (e) => {
      const {name, value, validity, validationMessage} = e.target;
      setInputs({...inputs, [name]: { value: value, isValid: validity.valid, }});
      setErrorMessages({...errorMessages, [name]: validationMessage});
      setErrorClasses({...errorClasses,
        [name]: { 
          input: !validity.valid ? popupInputClass.inputError : '', 
          error: !validity.valid ? popupInputClass.error : '', 
        }, 
      });
    };

    return (
      <PopupWithForm 
        name="card"
        title="Новое место" 
        button="card"
        buttonSubmitText={props.buttonSubmitText}
        submitDisabled={!inputs.title.isValid || !inputs.link.isValid}
        isOpen={props.isOpen} 
        onClose={props.onClose}
        onSubmit={handleSubmit}         
      >
        <input type="text"
            id="placename-input" name="name" 
            maxLength="30" minLength="2" 
            className={`popup__input popup__input_type_placename ${errorClasses.name.input}`} 
            placeholder="Название" 
            autoComplete="off"
            required
            value={inputs.name.value}
            onChange={handleChange}
            onInput={handleInput}
        />
          <span 
            className={`popup__input-error popup__input-error_active ${errorClasses.name.error}`} 
            id="placename-input-error"
          >
            {errorMessages.name}
          </span>

          <input type="url"
            id="imageLink-input" name="link" 
            pattern="https?://.+" 
            className={`popup__input popup__input_type_imagelink ${errorClasses.link.input}`} 
            placeholder="Ссылка на картинку" 
            required
            autoComplete="off"
            value={inputs.link.value}
            onChange={handleChange}
            onInput={handleInput}
          />

          <span 
            className={`popup__input-error popup__input-error_active ${errorClasses.link.error}`} 
            id="imageLink-input-error"
          >
            {errorMessages.link}
          </span>
      </PopupWithForm>
    );
  }
