function Main({props}) {
    const currentUser = React.useContext(CurrentUserContext);

    //---------------------------------------------------------------------------------------------------------------------

    return(
        <main className="content">
            <section className="profile">
                <button type="button" aria-label="открыть поле добавления аватара" className="profile__button" onClick={props.onEditAvatar}>
                    <img className="profile__avatar" src={`${currentUser.avatar}`} alt="Аватарка пользователя"/>
                </button>
                <div className="profile__info">
                    <h1 className="profile__user-name">{`${currentUser.name}`}</h1>
                    <button className="profile__edit-button" type="button" aria-label="открыть попап" onClick={props.onEditProfile}></button>
                    <p className="profile__occupation">{`${currentUser.about}`}</p>
                </div>
                <button className="profile__add-button" type="button" aria-label="открыть поле добавления фото" onClick={props.onAddPlace}></button>
            </section>

            <section className="elements">
                {props.cards.map((card) => (
                    <Card 
                        key={card._id}
                        card={card}
                        onCardClick={props.onCardClick}
                        onCardLike={props.onCardLike}
                        onCardDelete={props.onCardDelete}
                    />
                    )
                )}
            </section>
        </main>
    );
}

export default Main;
